import { decoder } from "tetris-fumen";
import { compressToEncodedURIComponent } from "lz-string";

export default async function parseReplay(txt: string) {
    const target = "jstris.jezevec10.com";
    if (txt.indexOf(target) < 0) return codeToFumen(txt);
    const parts = txt.split("/");
    if (parts.length < 5) {
        throw new Error("");
    }
    if (parts[3] != "replay" || !parts[2].endsWith(target)) {
        throw new Error("");
    }

    let id = parts[4];
    if (parts.length > 5) {
        id = parts[5];
    }

    const url = `https://corsproxy.io/?${encodeURIComponent(`https://${parts[2]}/replay/data?id=${id}&type=0`)}`;
    const response = await fetch(url);
    if (!response.ok) {
        throw new Error("");
    }
    const text = await response.text()
    const comp = compressToEncodedURIComponent(text);

    return await codeToFumen(comp);
    //return null;
}

function codeToFumen(replayCode: string) {
    fetch(`https://fumen.tstman.net/jstris`, {
        method: "POST",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
        },
        body: `replay=${replayCode}`,
    }).then((response) => {
        if (!response.ok) {
            throw new Error("");
        } else {
            response.json().then((data) => {
                return data.fumen;
                //return parseFumen(data.fumen);
            });
        }
    });
    //return null;
}
