import React, { useState, useRef, ReactNode } from "react";
import classes from "./Sidebar.module.css";

interface SetData {
    title: string;
    pfp: string;
    author: string;
    methods: string;
    setups: number;
    cover: string;
    description: string;
}

interface SidebarProps {
    title: string;
    children: ReactNode;
}

const Sidebar: React.FC<SidebarProps> = ({ title, children }) => {
    const [sidebarWidth, setSidebarWidth] = useState<number>(500); // Default width
    const sidebarRef = useRef<HTMLDivElement>(null); // Reference to sidebar
    const [isResizing, setIsResizing] = useState<boolean>(false); // Resizing state


    const startResizing = (e: React.MouseEvent) => {
        setIsResizing(true); // Enable resizing state
        document.addEventListener("mousemove", resize, false);
        document.addEventListener("mouseup", stopResizing, false);
    };
    const stopResizing = () => {
        setIsResizing(false);
        document.removeEventListener("mousemove", resize, false);
        document.removeEventListener("mouseup", stopResizing, false);
    };
    const resize = (e: MouseEvent) => {
        
        // Get window width
        const windowWidth = window.innerWidth;

        // Calculate new width from the right side
        
        const newWidth = windowWidth - e.clientX - 50;

        // Clamp the width to min and max
        setSidebarWidth(newWidth);
    };


    return (
        <div
            className={classes.sidebar}
            ref={sidebarRef}
            style={{ width: `${sidebarWidth}px`, userSelect:isResizing?'none':'auto' }} // Dynamic width
        >
            {/* Resize bar on the LEFT edge for right-side sidebar */}
            <div
                className={classes.vbar}
                onMouseDown={startResizing}
            ></div>

            <p className={classes.title}>{title}</p>
            <hr />

            <div className={classes.content}>
                {children}
            </div>
        </div>
    );
};

export default Sidebar;
