import { useState } from "react";

export const Fumen: FC<Props> = (props: Props) => {
    const { fumen, className, showComments = true } = props;
    let pages: Pages;
    try {
        pages = decoder.decode(fumen);
    } catch {
        return <></>;
    }

    const [pageIndex, setPageIndex] = useState(0);
    const currentPage = pages[pageIndex];

    return (
        <div className={className ? `${className} fumen` : "fumen"}>
            <FumenImage page={currentPage} />
            {showComments && currentPage.comment && (
                <div
                    style={{
                        marginTop: "0.5rem",
                        padding: "0.5rem",
                        backgroundColor: "#f9f9f9",
                        borderRadius: "4px",
                        fontFamily: "monospace",
                        whiteSpace: "pre-wrap",
                    }}
                >
                    {currentPage.comment}
                </div>
            )}
        </div>
    );
};
