  export default function pieces(queue: string, set: string): boolean {
    console.log(`Queue: ${queue}, Set: ${set}`);
    var [pieces, modifiers] = set.split('{');
    if(modifiers?.endsWith('}'))modifiers = modifiers.slice(0, modifiers.length - 1);
    console.log(`Pieces: ${pieces}, Modifiers: ${modifiers}`);
    
    const queuePieces = pieces.trim();
    return true;
  }
  
  
  
  console.log(pieces("IJLS", "[IJLS]!{S<3}"));
  */
export default checkPieces;
