const ORDER = "TIJLOSZ";

function sortByPCOrder(a: string, b: string) {
    return ORDER.indexOf(a) - ORDER.indexOf(b);
}

function getPermutations(str: string, length: number): string[] {
    const results: string[] = [];

    function backtrack(path: string[], used: boolean[]) {
        if (path.length === length) {
            results.push(path.join(""));
            return;
        }

        for (let i = 0; i < str.length; i++) {
            if (used[i]) continue;
            used[i] = true;
            path.push(str[i]);
            backtrack(path, used);
            path.pop();
            used[i] = false;
        }
    }

    backtrack([], Array(str.length).fill(false));
    return results;
}

function getCombinations(str: string, length: number): string[] {
    const sorted = [...str].sort(sortByPCOrder);
    const results: string[] = [];

    function backtrack(start: number, path: string[]) {
        if (path.length === length) {
            results.push(path.join(""));
            return;
        }
        for (let i = start; i < sorted.length; i++) {
            path.push(sorted[i]);
            backtrack(i + 1, path);
            path.pop();
        }
    }

    backtrack(0, []);
    return results;
}

export default function pieces(input: string): string[] {
    const parts: string[][] = [];
    const regex = /\[([A-Z]+)\](p|c)(\d)|[A-Z]+/g;
    let match;

    while ((match = regex.exec(input)) !== null) {
        if (match[1]) {
            const chars = match[1];
            const type = match[2];
            const amount = parseInt(match[3]);
            if (type === "p") {
                parts.push(getPermutations(chars, amount));
            } else {
                parts.push(getCombinations(chars, amount));
            }
        } else {
            parts.push([match[0]]); // Fixed pieces like "T"
        }
    }

    // Cartesian product of all parts
    function cartesianProduct(arrays: string[][]): string[] {
        return arrays.reduce((acc, curr) => {
            const result: string[] = [];
            for (const a of acc) {
                for (const b of curr) {
                    result.push(a + b);
                }
            }
            return result;
        }, [""]);
    }

    return cartesianProduct(parts);
}

function checkPieces(queue: string, pattern: string): boolean {
    //Fix queue (remove unnessesary pieces, replace * with [TIJLOSZ].)
    pattern = pattern.replaceAll(/\*/g, "[TIJLOSZ]").replaceAll(/[^\[\]^!TIJLOSZ0-7]/g, "");


    //Iterate through each bag and compare to queue
    for (let [_, set, count = 1] of Array.from(pattern.matchAll(/((?<=\[)\^?[TIJLOSZ]{0,7}(?=\])|[TIJLOSZ])\]?(?:([!0-7]))?/g))) {
        set = set.startsWith("^") ? "TIJLOSZ".split("").filter((piece) => !set.includes(piece)).join("") : set;
        count = count === "!" ? set.length : Number(count);
        if(queue.length < count || set.length < count)return false;

        for (let piece of queue.substring(0, count)) {
            if (!set.includes(piece)) return false;
            set = set.replace(piece, "");
        }
        queue = queue.substring(count, queue.length);
    }

    return !queue.length;
}

function checkModifiers(queue: string, pattern: string): boolean {
    
    
    return false;
}

