import React, { useState, useRef, ReactNode } from "react";
import classes from "./Sidebar.module.css";

interface SetData {
    title: string;
    pfp: string;
    author: string;
    methods: string;
    setups: number;
    cover: string;
    description: string;
}

interface SidebarProps {
    title: string;
    children: ReactNode;
}

const Sidebar: React.FC<SidebarProps> = ({ title, children }) => {
    const [sidebarWidth, setSidebarWidth] = useState<number>(500);
    const sidebarRef = useRef<HTMLDivElement>(null);
    const [isResizing, setIsResizing] = useState<boolean>(false);


    const startResizing = (e: React.MouseEvent) => {
        setIsResizing(true);
        document.addEventListener("mousemove", resize, false);
        document.addEventListener("mouseup", stopResizing, false);
    };
    const stopResizing = () => {
        setIsResizing(false);
        document.removeEventListener("mousemove", resize, false);
        document.removeEventListener("mouseup", stopResizing, false);
    };
    const resize = (e: MouseEvent) => {
        setSidebarWidth(window.innerWidth - e.clientX - 53);
    };


    return (
        <div className={classes.sidebar} ref={sidebarRef} style={{ width: `${sidebarWidth}px`, userSelect:isResizing?'none':'auto' }}>
            <div className={classes.vbar} onMouseDown={startResizing}></div>

            <h1 className={classes.title}>{title}</h1>
            <hr />

            <>{children}</>
        </div>
    );
};

export default Sidebar;
