function checkPieces(queue: string, pieces: string): boolean {
    //Fix queue (remove unnessesary pieces, replace * with [TIJLOSZ].)
    pieces = pieces.replaceAll(/[^\[\]^!TIJLOSZ0-7]/g, "").replaceAll("*", "[TIJLOSZ]");

    for (let [_, set, count = 1] of Array.from(pieces.matchAll(/((?<=\[)[TIJLOSZ]{0,7}(?=\])|[TIJLOSZ])\]?(?:([!0-7]))?/g))) {
        if (set.startsWith("^"))
            set = "TIJLOSZ"
                .split("")
                .filter((piece) => !set.includes(piece))
                .join("");
        count = count === "!" ? set.length : Number(count);

        for (let piece of queue.substring(0, count)) {
            // GOOD !
            if (!set.includes(piece)) return false;
            set = set.replace(piece, "");
        }
        queue = queue.substring(count, queue.length);
    }

    return !queue.length;
}

export default function pieces(queue: string, set: string): boolean {
    console.log(`Queue: ${queue}, Set: ${set}`);
    var [pieces, modifiers] = set.split("{");
    if (modifiers?.endsWith("}"))
        modifiers = modifiers.slice(0, modifiers.length - 1);
    console.log(`Pieces: ${pieces}, Modifiers: ${modifiers}`);

    const queuePieces = pieces.trim();
    return true;
}
