import { PC } from "./PC";
import { PCGroup } from "./PCGroup";
import { pieces } from "./pieces";

class Setup {
    public static testSetups = [
        new Setup({
            name: "See Z",
            setupFumen: "v115@HhglIeglCeR4Aei0hlAeR4zhg0JeAgWHAT4UTAS4CC?A",
            solutionFumen: "v115@9ghlwhh0R4AtRpA8glwhg0R4BtRpA8glwhg0B8AtE8?whG8JeAgH",
            PCGroup: PCGroup.from("IJLS")!,
            success: ["*p3{Z<S}"],
            fails: ["[ST]!Z"],
        }),
        new Setup({
            name: "See SZ",
            setupFumen: "v115@LhhlDeg0zhglBeR4i0BeglAeR4KeAgH",
            solutionFumen: "v115@9gRpzhh0hlRpBtB8g0R4glF8R4E8BtA8g0B8glJeAg?H",
            PCGroup: PCGroup.from("IJLS")!,
        }),
        new Setup({
            name: "Cradle",
            setupFumen: "v115@DhBthlGeBtglGeBtglDezhBtJeAgH",
            solutionFumen: "v115@DhD8GeC8GeC8DeF8JeAgH",
            PCGroup: PCGroup.from("Z")!,
        }),
        new Setup({
            name: "use SZ",
            setupFumen: "v115@IgAAgH",
            solutionFumen: "v115@MgAAgH",
            PCGroup: PCGroup.from("LO")!,
        }),
        new Setup({
            name: "PCO",
            setupFumen: "v115@9gilEeR4glRpDeR4wwg0RpCeBtxwi0DeBtwwJeAgH",
            solutionFumen: "v115@vhAAgH",
            PCGroup: PCGroup.from("TIJLOSZ")!,
        }),
        new Setup({
            name: "AntiPCO",
            setupFumen: "v115@WgAAgH",
            solutionFumen: "v115@XgAAgH",
            PCGroup: PCGroup.from("TIJLSZ")!,
        }),
        new Setup({
            name: "Zed Setup",
            setupFumen: "v115@UgAAgH",
            solutionFumen: "v115@VgAAgH",
            PCGroup: PCGroup.from("Z")!,
        }),
        new Setup({
            name: "Corner Cover",
            setupFumen: "v115@PgAAgH",
            solutionFumen: "v115@QgAAgH",
            PCGroup: PCGroup.from("IJLSTZ")!,
        }),
        new Setup({
            name: "3p",
            setupFumen: "v115@OgAAgH",
            solutionFumen: "v115@NgAAgH",
            PCGroup: PCGroup.from("ILS")!,
        }),
        new Setup({
            name: "",
            setupFumen: "v115@YgAAgH",
            solutionFumen: "v115@ZgAAgH",
            PCGroup: PCGroup.from("LTJS")!,
        }),
    ];

    protected name: string;
    protected setupFumen: string;
    protected solutionFumen: string;
    protected success: Set<string> | undefined;
    protected fails: Set<string> | undefined;
    protected PCGroup: PCGroup;

    constructor({
        name = "Unnamed Setup",
        setupFumen = "v115@vhAAgH",
        solutionFumen = "v115@vhAAgH",
        success,
        fails,
        PCGroup,
    }: {
        name: string;
        setupFumen: string;
        solutionFumen: string;
        success?: string[];
        fails?: string[];
        PCGroup: PCGroup;
    }) {
        this.name = name;
        this.setupFumen = setupFumen;
        this.solutionFumen = solutionFumen;
        this.success = success ? this.preprocessQueues(success) : undefined;
        this.fails = fails ? this.preprocessQueues(fails) : undefined;
        this.PCGroup = PCGroup;
    }

    // Preprocess queues using the `pieces` function
    private preprocessQueues(queues: string[]): Set<string> {
        const result = new Set<string>();
        for (const queue of queues) {
            for (const expanded of pieces(queue)) {
                result.add(expanded);
            }
        }
        return result;
    }

    // Static method to calculate coverage for a PCGroup
    public static calculateCoverage(pcGroup: PCGroup): number {
        const permutations = pieces(`[${pcGroup.getQueue()}]!p3`); // Generate all permutations for the PCGroup
        let matchingCount = 0;

        for (const permutation of permutations) {
            for (const setup of Setup.testSetups) {
                if (setup.getSuccess()?.has(permutation)) {
                    matchingCount++;
                    break; // Stop checking other setups for this permutation
                }
            }
        }

        return (matchingCount / permutations.size) * 100; // Return progress as a percentage
    }

    // Getters
    public getName() {
        return this.name;
    }
    public getSetup() {
        return this.setupFumen;
    }
    public getSolution() {
        return this.solutionFumen;
    }
    public getFails() {
        return this.fails;
    }
    public getSuccess() {
        return this.success;
    }
    public getPCGroup() {
        return this.PCGroup;
    }
}

export default Setup;
