// src/components/Sidebar.tsx
import React, { useState, useRef } from "react";
import classes from "./Sidebar.module.css";

// ✅ Define the set type
interface SetData {
    title: string;
    pfp: string;
    author: string;
    methods: string;
    setups: number;
    cover: string;
    description: string;
}

// ✅ Define props
interface SidebarProps {
    title: string;
    sets: SetData[];
}

const Sidebar: React.FC<SidebarProps> = ({ title, sets }) => {
    const [sidebarWidth, setSidebarWidth] = useState<number>(650); // Default width
    const sidebarRef = useRef<HTMLDivElement>(null); // Sidebar reference
    const [isResizing, setIsResizing] = useState<boolean>(false); // Resizing state

    // ✅ Start resizing
    const startResizing = (e: React.MouseEvent) => {
        setIsResizing(true); // Enable resizing state
        document.body.style.userSelect = "none"; // Prevent text selection
        document.addEventListener("mousemove", resize, false);
        document.addEventListener("mouseup", stopResizing, false);
    };

    // ✅ Resize handler
    const resize = (e: MouseEvent) => {
        if (!isResizing) return;

        // Get window width
        const windowWidth = window.innerWidth;
        const newWidth = Math.min(
            Math.max(windowWidth - e.clientX, 300), // Min width
            800 // Max width
        );

        setSidebarWidth(newWidth);
    };

    // ✅ Stop resizing
    const stopResizing = () => {
        setIsResizing(false);
        document.body.style.userSelect = "auto"; // Re-enable text selection
        document.removeEventListener("mousemove", resize, false);
        document.removeEventListener("mouseup", stopResizing, false);
    };

    return (
        <div
            className={classes.sidebar}
            ref={sidebarRef}
            style={{
                width: `${sidebarWidth}px`,
                userSelect: isResizing ? "none" : "auto", // Apply CSS during resize
            }}
        >
            {/* Resize handle on the left edge */}
            <div
                className={classes.vbar}
                onMouseDown={startResizing}
            ></div>

            <p className={classes.title}>{title}</p>
            <hr />

            <div className={classes.content}>
                {sets.map((set, index) => (
                    <div key={index} className={classes.setCard}>
                        <h2 className={classes.setTitle}>{set.title}</h2>
                        <p className={classes.setDescription}>{set.description}</p>
                        <div className={classes.setAuthor}>
                            <img src={set.pfp} alt="" />
                            <a>{set.author}</a>
                        </div>
                        <hr />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Sidebar;
