function checkPieces(queue: string, pieces: string): boolean {
    console.log(`Queue: ${queue}`);
    console.log(`Pieces: ${pieces}`);
    
    // Remove non-useful characters (mostly [])
    pieces = pieces.replaceAll("*", "[TIJLOSZ]");
    pieces = pieces.replaceAll(/[^\[\]^!TIJLOSZ0-7]/g, "");

    let i=0;

    for (let [_, set, count = 1] of Array.from(pieces.matchAll(/((?<=\[)[TIJLOSZ]{0,7}(?=\])|[TIJLOSZ])\]?(?:([!0-7]))?/g))) {
        
        if (set.startsWith("^")) set = "TIJLOSZ".split("").filter(piece => !set.includes(piece)).join("");
        count = count === "!" ? set.length : Number(count);

        console.log(set,count);
        for(i=i;i<count;i++){
            console.log(set, queue, queue[i], count);
            
            if(!set.includes(queue[i]))return false;


        }
        
    }

    return true;
}

