// src/components/Sidebar.tsx
import React from "react";
import classes from "./Sidebar.module.css";

// ✅ Define the set type
interface SetData {
    title: string;
    pfp: string;
    author: string;
    methods: string;
    setups: number;
    cover: string;
    description: string;
}

// ✅ Define props
interface SidebarProps {
    title: string;
    sets: SetData[];
}

const Sidebar: React.FC<SidebarProps> = ({ title, sets }) => {
    const resize = function() { 
        console.log("hi");
        
    };
    return (
        <>
            <div className={classes.sidebar}>
                <div className={classes.vbar}></div>
                <p className={classes.title}>{title}</p>
                <hr />
                <div className={classes.content}>
                    {sets.map((set, index) => (
                        <div key={index} className={classes.setCard}>
                            <h2 className={classes.setTitle}>{set.title}</h2>
                            <p className={classes.setDescription}>
                                {set.description}
                            </p>
                            <div className={classes.setAuthor}>
                                <img src={set.pfp} alt="" />
                                <a>{set.author}</a>
                            </div>
                            <hr />
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
};

export default Sidebar;
