import { compressToEncodedURIComponent } from "lz-string";

export default async function getFumenFromReplay(replayUrl: string): Promise<string | null> {
    // Check if the URL is a valid Jstris replay URL
    const target = "jstris.jezevec10.com";
    if (!replayUrl.includes(target)) {
        throw new Error(
            "Invalid replay URL. It must be from jstris.jezevec10.com."
        );
    }

    const parts = replayUrl.split("/");
    if (parts.length < 5 || parts[3] !== "replay") {
        throw new Error("Invalid replay URL format.");
    }

    // Extract replay ID
    const id = parts[4];

    // Fetch replay data using CORS proxy
    const URL = `https://jstris.jezevec10.com/replay/data?id=${id}&type=0`
    const proxyUrl = `https://api.allorigins.win/get?url=${encodeURIComponent(URL)}`;

    console.log(proxyUrl);

    const response = await fetch(proxyUrl);
    if (!response.ok) throw new Error("Network response was not ok.");
    const data = await response.json();
    
    const contents = data.contents.json();
    
    return null;
    

    /*
  
  try {
    const replayResponse = await fetch(proxyUrl);
    if (!replayResponse.ok) {
      throw new Error("Failed to fetch replay data.");
    }

    const replayData = await replayResponse.text();

    // Compress the replay data for Fumen API compatibility
    const compressedReplay = compressToEncodedURIComponent(replayData);

    // Send the compressed replay to the Fumen API
    const fumenResponse = await fetch(`https://fumen.tstman.net/jstris`, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ replay: compressedReplay }),
    });

    if (!fumenResponse.ok) {
      throw new Error("Failed to convert replay to Fumen.");
    }

    const fumenData = await fumenResponse.json();

    // Return the Fumen string
    return fumenData.fumen;
  } catch (error) {
    console.error("Error processing replay:", error);
    return null;
  }
    */
}
