import React, { useState, useRef, useEffect } from "react";
import classes from "./Sidebar.module.css";

// ✅ Define the set type
interface SetData {
    title: string;
    pfp: string;
    author: string;
    methods: string;
    setups: number;
    cover: string;
    description: string;
}

// ✅ Define props
interface SidebarProps {
    title: string;
    sets: SetData[];
}

const Sidebar: React.FC<SidebarProps> = ({ title, sets }) => {
    const [sidebarWidth, setSidebarWidth] = useState<number>(650); // Default width
    const sidebarRef = useRef<HTMLDivElement>(null); // Reference to sidebar

    // ✅ Handles resizing
    const startResizing = (e: React.MouseEvent) => {
        document.addEventListener("mousemove", resize);
        document.addEventListener("mouseup", stopResizing);
    };

    // ✅ Resizes sidebar
    const resize = (e: MouseEvent) => {
        // Calculate the new width
        
        const newWidth = Math.max(300, Math.min(e.clientX, 800)); // Clamp between 300 and 800
        console.log(newWidth);
        setSidebarWidth(newWidth); // ✅ Update state, triggers re-render
    };

    // ✅ Removes event listeners
    const stopResizing = () => {
        document.removeEventListener("mousemove", resize);
        document.removeEventListener("mouseup", stopResizing);
    };

    // ✅ Cleanup in case component unmounts mid-resize
    useEffect(() => {
        return () => {
            document.removeEventListener("mousemove", resize);
            document.removeEventListener("mouseup", stopResizing);
        };
    }, []);

    return (
        <div
            className={classes.sidebar}
            ref={sidebarRef}
            style={{ width: `${sidebarWidth}px` }} // Dynamic width
        >
            {/* Resize bar */}
            <div
                className={classes.vbar}
                onMouseDown={startResizing}
            ></div>

            <p className={classes.title}>{title}</p>
            <hr />

            <div className={classes.content}>
                {sets.map((set, index) => (
                    <div key={index} className={classes.setCard}>
                        <h2 className={classes.setTitle}>{set.title}</h2>
                        <p className={classes.setDescription}>{set.description}</p>
                        <div className={classes.setAuthor}>
                            <img src={set.pfp} alt="" />
                            <a>{set.author}</a>
                        </div>
                        <hr />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Sidebar;
