function checkPieces(queue: string, pieces: string): boolean {
    // Parse the pieces string to get the sets and their counts
    pieces = pieces.replaceAll("*", "[TIJLOSZ]");

    // Iterate through the matches
    for (let [_, set, count = 1] of Array.from(pieces.matchAll(/\[(\^?[TIJLOSZ]{0,7})\](?=p([0-7]))?/g))) {
        count = Number(count);
        if (set.startsWith("^")) {
            const allPieces = "TIJLOSZ".split("");
            const excludedPieces = set.slice(1).split("");
            set = allPieces.filter(
                (piece) => !excludedPieces.includes(piece)
            ).join("");
        }

        console.log(set, count);
        

        // Process the set and count as needed
    }

    return true; // or your actual logic
}

